import React, { Component } from 'react';
import Title from './components/Title';
import Control from './components/Control';
import Form from './components/Form';
import List from './components/List';
import task from './mocks/Task';

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            items: task,
            isShowForm: false,
            strSearch : ''
        };

        this.handleToogleForm = this.handleToogleForm.bind(this);
        this.closeForm = this.closeForm.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
    }

    handleToogleForm() {
        this.setState({
            isShowForm: !this.state.isShowForm
        });
    }

    handleSearch(value) {
        this.setState({
            strSearch: value
        });
    }

    closeForm() {
        this.setState({
            isShowForm: false
        });
    }

    render() {
        console.log("strSearch: ", this.state.strSearch);
        let itemsOrigin = this.state.items;
        let items = [];
        let isShowForm = this.state.isShowForm;
        let elmForm = null;
        const search = this.state.strSearch;
        itemsOrigin.forEach((item) => {
            if (item.name.toLowerCase().indexOf(search) !== -1) {
                items.push(item);
            }
        });

        if (isShowForm) {
            elmForm = <Form onClickCancel={this.closeForm}/>;
        }
        return (
            <div>
                <Title/>
                {/* TITLE : END */}
                {/* CONTROL (SEARCH + SORT + ADD) : START */}
                <Control onClickSearchGo={this.handleSearch} onClickAdd = {this.handleToogleForm} isShowForm={isShowForm}/>
                {/* CONTROL (SEARCH + SORT + ADD) : END */}
                {/* FORM : START */}
                {elmForm}
                {/* FORM : END */}
                {/* LIST : START */}
                <List items ={items}/>
			</div>
        );
    }
}

export default App;
