import React, { Component } from 'react';

class Sort extends Component {
    constructor(props) {
        super(props);

        this.state = {
        };

        this.handleSort1 = this.handleSort1.bind(this);
    }

    handleSort1(orderBy, orderDir) {
        console.log("handleSort:", orderBy + "-" + orderDir);
        this.props.onClickSort(orderBy, orderDir);
    }
    render() {

        let {orderBy, orderDir} = this.props;
        let strSort = orderBy + " - " + orderDir;
        console.log(strSort);
        return (
            <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
                <div className="dropdown">
                    <button
                    className="btn btn-default dropdown-toggle"
                    type="button"
                    id="dropdownMenu1"
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="true"
                    >
                    Sort by <span className="caret" />
                    </button>
                    <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
                    <li>
                        <a onClick={() =>this.handleSort1('name', 'asc')}>Name ASC</a>
                    </li>
                    <li>
                        <a onClick={() =>this.handleSort1('name', 'desc')}>Name DESC</a>
                    </li>
                    <li role="separator" className="divider" />
                    <li>
                        <a onClick={() =>this.handleSort1('level', 'asc')}>Level ASC</a>
                    </li>
                    <li>
                        <a onClick={() =>this.handleSort1('level', 'desc')}>Level DESC</a>
                    </li>
                    </ul>
                    <span className="label label-success label-medium">{ strSort }</span>
                </div>
            </div>
        );
    }
}

export default Sort;
